INSERT INTO `cm_article` VALUES ('8a3080d54c406b73014c5107b2530569', null, 'Samsung Android Phone', null, '<div><b>Introduce</b></div><div>Samsung Galaxy (stylized as Samsung GALAXY) is a series of Android-powered mobile computing devices designed, manufactured and marketed by Samsung Electronics. The product line includes the Galaxy S series of high-end smartphones, the Galaxy Note series of tablets and phablets with the added functionality of a stylus and the Galaxy Tab series of tablets.</div><div><br></div><div><b>Review Videos</b><br></div><div><a href=\"http://www.youtube.com/watch?v=nl9n3xlEnJA\">Samsung Galaxy Note 3 Review</a><br></div><div><a href=\" http://www.youtube.com/watch?v=tTxhneVcmEo\">Samsung Galaxy Round Review</a><br></div><div><a href=\"http://www.youtube.com/watch?v=JGvhMTS7e7U\">Samsung Galaxy S4 ACTIVE Review</a><br></div><div><a href=\"http://www.youtube.com/watch?v=7OgxWJ4Ewx8\">Samsung Galaxy S4 Zoom Review</a><br></div><div><a href=\"http://www.youtube.com/watch?feature=player_embedded&amp;v=U8STgCviLe0\">Samsung Galaxy S4 Review</a><br></div><div><a href=\"http://www.youtube.com/watch?v=VdsMwGWvhO4\">Samsung Galaxy Note 2 Review</a><br></div><div><a href=\"http://www.youtube.com/watch?v=8UjnBU2BueQ\">Samsung Galaxy S3 Official Review</a><br></div><div><br></div><div><iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/nl9n3xlEnJA?wmode=opaque\" data-youtube-id=\"nl9n3xlEnJA\" frameborder=\"0\" allowfullscreen=\"\"></iframe><br></div><p><br></p><p><iframe width=\"560\" height=\"315\" src=\"http://www.youtube.com/embed/tTxhneVcmEo?wmode=opaque\" data-youtube-id=\"tTxhneVcmEo\" frameborder=\"0\" allowfullscreen=\"\">&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;lt;DIV&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;gt;&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;lt;br&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;gt;&amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;amp;lt;/DIV&amp;amp;amp;amp;amp