INSERT INTO `cm_image` VALUES ('8a3080d54c51a593014c561f45f10250', null, 'Singapore MRT Map', '/resources/upload/images/Singapore_MRT_Map_1280_800.png', '', '<p style=\"line-height: 16.7999992370605px;\">Singapore&nbsp;<b>Mass Rapid Transit</b>&nbsp;or MRT<br></p><p style=\"line-height: 16.7999992370605px;\">The initial section of the MRT, between&nbsp;<u>Yio Chu Kang</u>&nbsp;and&nbsp;<u>Toa Payoh</u>, opened in&nbsp;<u>1987</u>, making it the second-oldest metro system in Southeast Asia, after Manila LRT System.&nbsp;</p><p style=\"line-height: 16.7999992370605px;\">North South Line (red) :&nbsp;1987/11,&nbsp;26 stations,&nbsp;45km, SMRT Trains</p><p style=\"line-height: 16.7999992370605px;\">East West Line (green) : 1987/12,&nbsp;35 stations,&nbsp;57.2km, SMRT Trains<br></p><p style=\"line-height: 16.7999992370605px;\">North East Line (purple) : 2003/06,&nbsp;16 stations,&nbsp;16km, SBS Trainsit<br></p><p style=\"line-height: 16.7999992370605px;\">Circle Line (orange) : 2009/05,&nbsp;30 stations,&nbsp;35.7km, SMRT Trains<br></p><p style=\"line-height: 16.7999992370605px;\">Downtown Line (blue) : 2013/12,&nbsp;6 stations,&nbsp;4.3km, SBS Trainsit</p>', 'Singapore', null, '9999', 'en_SG', null, null, '2015-03-26 20:47:12', '2015-03-28 09:54:57', '0', '0', null, null);
