INSERT INTO `cm_image` VALUES ('8a3080d54c51a593014c5e9a119f47e2', null, 'Fjallraven', '/resources/upload/images/Fjallraven_Shop_1280_800.jpg', '', '<p style=\"line-height: 16.7999992370605px;\"><b>Fjallraven</b>&nbsp;(Arctic Fox)<br></p><p style=\"line-height: 16.7999992370605px;\">Founded :&nbsp;1960</p><p style=\"line-height: 16.7999992370605px;\">Headquarters :&nbsp;Örnsköldsvik, Sweden</p><p style=\"line-height: 16.7999992370605px;\">Fjällräven is a Swedish company specialising in outdoor equipment.&nbsp;Fjällräven was founded in 1960 by&nbsp;<u>Åke Nordin</u>&nbsp;(1936–2013). The original product was an external frame&nbsp;<u>rucksack.</u>&nbsp;The Fjällräven rucksack proved comfortable as well as durable, and along with outdoor clothing made from tent fabric it made the brand well known in just a decade. It is also represented in other European countries and in particular Germany. Among the more well-known products are Greenland jackets and various versions of the Kånken rucksack.<br></p><div><br></div>', 'Retail,Clothing,Outdoor Gear,50s', null, '9999', 'en_SG', null, null, '2015-03-28 12:18:17', '2015-03-28 12:18:17', '0', '0', null, null);
INSERT INTO `cm_image` VALUES ('8a3080d54c51a593014c5e9a89de4852', null, 'Samwer Brothers', '/resources/upload/images/People_Samwer_Brothers_1280_800.jpg', '', '<p style=\"line-height: 16.7999992370605px;\"><b>Marc / Oliver /&nbsp;<span style=\"font-size: 14.3999996185303px; line-height: 16.7999992370605px;\">Alexander</span><span style=\"line-height: 1.5;\">&nbsp;Samwer Brothers</span></b></p><p style=\"line-height: 16.7999992370605px;\">In 2013 Samwer brothers founded EURO 150 Mio. Global Founders Capital fund for early stage startups worldwide. Hamburger technology startup Kreditech became their first investment.&nbsp;They also founded the start-up incubator&nbsp;<u>Rocket Internet</u>. They invested in Facebook in 2008. Zalora, Lazada, Foodpanda.<br></p><div><br></div>', 'People,Germany', null, '9999', 'en_SG', null, null, '2015-03-28 12:18:48', '2015-03-28 12:20:00', '0', '0', null, null);
